Properties.Octopus.Action.Script.ScriptBody:
$region = $OctopusParameters['Region']
$folder = $OctopusParameters['Folder']
$bucket = $OctopusParameters['Bucket']
$accessKey = $OctopusParameters['S3AccessKey']
$secretKey = $OctopusParameters['S3SecretKey']

Set-AWSCredentials -AccessKey $accessKey -SecretKey $secretKey -StoreAs S3Creds
Set-DefaultAWSRegion -Region $region

Function Upload($item) {
    foreach ($i in Get-ChildItem $item)
    {
        if((Get-Item $i.FullName) -is [System.IO.DirectoryInfo]){
            Write-S3Object -ProfileName S3Creds -BucketName $bucket -KeyPrefix $i -Folder $i.FullName
        } else {
            Write-S3Object -ProfileName S3Creds -BucketName $bucket -Key $i -File $i.FullName
        }
    }
}

Upload($folder)
