Properties.Octopus.Action.Script.ScriptBody:
$fileName = $OctopusParameters['FileName']
$path = $OctopusParameters['Path']
$content = $OctopusParameters['Content']

if ($fileName -and $path) {

 if(-Not(Test-Path $path))
    {
       Write-Error "$path does not exist"
       exit 1
    }

    $fullPath=Join-Path $path -ChildPath $fileName

    Write-Host "File Path: $fullPath"

    New-Item -type file -Path $fullPath -force

    if($content){
       Add-Content $fullPath $content
    }

    Write-Host "$fileName created at $path"
}
else{
    Write-Error "File creation failed : Incorrect File name/path."
    exit 1
}
