Properties.Octopus.Action.Script.ScriptBody:
$ErrorActionPreference = "Stop"
[System.Reflection.Assembly]::LoadWithPartialName("Microsoft.Web.Administration")

# set up params
$octopusUrl = $OctopusParameters['OctopusUrl']
$apiKey = $OctopusParameters['ViewOnlyApiKey']
$envId = $OctopusParameters['Octopus.Environment.Id']
$roleName = "arr-server"
$lwebfarmname = $OctopusParameters['WebFarmName']
$lsitebinding = $OctopusParameters['ServerAddress']
$lnewState = $OctopusParameters['ARRAction']

# fetch ARR servers
$envUrl = "http://" + $octopusUrl + "/api/environments/" + $envId + "/machines"
$arrServers = ((Invoke-WebRequest -Uri $envUrl -Method Get -Headers @{"X-Octopus-ApiKey"=$apiKey}).Content | ConvertFrom-Json).Items | Where-Object { $_.Roles -eq $roleName }
$arrServerNames = $machines | Select -ExpandProperty Name

# start of add to load
$ErrorActionPreference = "SilentlyContinue"
[array]$errors
foreach ($arrName in $arrServerNames)
{
	Write-Output "Calling arr server $arrName $lwebfarmname $lsitebinding with new state $lnewState"
	$serverManager = New-Object Microsoft.Web.Administration.ServerManager
	$serverManager = [Microsoft.Web.Administration.ServerManager]::OpenRemote($arrName)
	try
	{
		$webFarm = $serverManager.GetApplicationHostConfiguration().GetSection("webFarms").GetCollection() | Where-Object { $_.GetAttributeValue("name") -eq $lwebfarmname }
		$serverInstance = $webFarm.GetCollection() | Where-Object { $_.GetAttributeValue("address") -eq $lsitebinding }
		$moop = $serverInstance.GetChildElement("applicationRequestRouting").Methods["SetState"].CreateInstance()
		$moop.Input.SetAttributeValue("newState", $lnewState)
		$moop.Execute()
	}
	catch
	{
		$errors + "Error calling arr server $arrName $lwebfarmname $lsitebinding $Error[0].Exception.Message"
	}
	finally
	{
		if ($serverManager -ne $null)
		{
			$serverManager.Dispose()
		}
	}
}
if ($errors.Count -gt 0)
{
	Write-Error $errors
	exit 1
}
else
{
	Write-Output "Finished arr state change"
}
$ErrorActionPreference = "Stop"
