Properties.Octopus.Action.Script.ScriptBody:
# Version date: 17th July, 2017
$ErrorActionPreference = 'Stop'
$VerbosePreference = 'Continue'

# Set process level FUR environment
$env:REDGATE_FUR_ENVIRONMENT = "Octopus Step Templates"

# Check if DLM Automation is installed.
$dlmAutomationModule = Get-Module -ListAvailable -Name DLMAutomation
if ($dlmAutomationModule -eq $null) { 
    throw "Cannot find DLM Automation on your Octopus Tentacle. If DLM Automation is installed, try restarting the Tentacle service for it to be detected."
}
$currentVersion = $dlmAutomationModule.Version
$minimumRequiredVersion = [version] '1.5.0.0'
if ($currentVersion -lt $minimumRequiredVersion) { 
    throw "This step requires DLM Automation version $minimumRequiredVersion or later. The current version is $currentVersion. The latest version can be found at http://www.red-gate.com/dlmas/download"
}

# Check the parameters.
if ([string]::IsNullOrWhiteSpace($DLMAutomationDatabaseName)) { throw "You must enter a value for 'Target database name'." }
if ([string]::IsNullOrWhiteSpace($DLMAutomationDatabaseServer)) { throw "You must enter a value for 'Target SQL Server instance'." } 
if ([string]::IsNullOrWhiteSpace($DLMAutomationNuGetDbPackageDownloadStepName)) { throw "You must enter a value for 'Database package step'." }
if ([string]::IsNullOrWhiteSpace($DLMAutomationFilterPath)) { $DLMAutomationFilterPath = $null } 
if ([string]::IsNullOrWhiteSpace($DLMAutomationCompareOptions)) { $DLMAutomationCompareOptions = $null } 

# Get the NuGet package installation directory path.
$packagePath = $OctopusParameters["Octopus.Action[$DLMAutomationNuGetDbPackageDownloadStepName].Output.Package.InstallationDirectoryPath"]
if($packagePath -eq $null) {
    throw "The 'Database package download step' is not a 'Deploy a NuGet package' step: '$DLMAutomationNuGetDbPackageDownloadStepName'"
}

# Work out where the scripts folder from the downloaded NuGet package is.
$databaseStatePath = Join-Path -Path $packagePath -ChildPath 'db\state'

# Constructing the unique export path.
$projectId = $OctopusParameters["Octopus.Project.Id"]
$releaseNumber = $OctopusParameters["Octopus.Release.Number"]
$nugetPackageId = $OctopusParameters["Octopus.Action[$DLMAutomationNuGetDbPackageDownloadStepName].Package.NuGetPackageId"]
$exportPath = Join-Path (Join-Path (Join-Path $DLMAutomationDeploymentResourcesPath $projectId) $releaseNumber) $nugetPackageId

# Make sure the directory we're about to create doesn't already exist, and delete any files if requested.
if ((Test-Path $exportPath) -AND ((Get-ChildItem $exportPath | Measure-Object).Count -ne 0)) {
    if ($DLMAutomationDeleteExistingFiles -eq 'True') {
        Write-Host "Deleting all files in $exportPath"
        rmdir $exportPath -Recurse -Force
    } else {
        throw "The export path is not empty: $exportPath.  Select the 'Delete files in export folder' option to overwrite the existing folder contents."
    }
}

# Determine whether or not to include identical objects in the report.
$DLMAutomationIncludeIdenticalsInReport = $DLMAutomationIncludeIdenticalsInReport -eq "True"

$targetDB = New-DlmDatabaseConnection -ServerInstance $DLMAutomationDatabaseServer -Database $DLMAutomationDatabaseName -Username $DLMAutomationDatabaseUsername -Password $DLMAutomationDatabasePassword | Test-DlmDatabaseConnection
$ignoreStaticData = $DLMAutomationIgnoreStaticData -eq "True"

# Create the deployment resources from the database to the NuGet package
$release = New-DlmDatabaseRelease -Target $targetDB `
                                  -Source $databaseStatePath `
                                  -TransactionIsolationLevel $DLMAutomationTransactionIsolationLevel `
                                  -IgnoreStaticData:$ignoreStaticData `
                                  -FilterPath $DLMAutomationFilterPath `
                                  -SQLCompareOptions $DLMAutomationCompareOptions `
                                  -IncludeIdenticalsInReport:$DLMAutomationIncludeIdenticalsInReport

# Export the deployment resources to disk
$release | Export-DlmDatabaseRelease -Path $exportPath

# Import the changes summary, deployment warnings, and update script as Octopus artifacts, so you can review them.
New-OctopusArtifact "$exportPath\Reports\Changes.html" -Name "Changes-$DLMAutomationDatabaseName.html"
New-OctopusArtifact "$exportPath\Reports\Warnings.xml" -Name "Warnings-$DLMAutomationDatabaseName.xml"
New-OctopusArtifact "$exportPath\Update.sql" -Name "Update-$DLMAutomationDatabaseName.sql"

# Sets a variable if there are changes to deploy. Useful if you want to have steps run only when this is set
if ($release.UpdateSQL -notlike '*This script is empty because the Target and Source schemas are equivalent*')
{
	Set-OctopusVariable -name "ChangesToDeploy" -value "True"
}
