Properties.Octopus.Action.Script.ScriptBody:
# Check if SQL Release is installed.
$sqlReleaseModule = Get-Module -ListAvailable -Name SQLRelease
if ($sqlReleaseModule -eq $null) { 
    throw "Cannot find SQL Release on your Octopus Tentacle. If SQL Release is installed, try restarting the Tentacle service for it to be detected."
}
$currentVersion = $sqlReleaseModule.Version
$minimumRequiredVersion = [version] '1.2.1.2943'
if ($currentVersion -lt $minimumRequiredVersion) { 
    throw "This step requires SQL Release version $minimumRequiredVersion or later. The current version is $currentVersion. The latest version can be found at http://www.red-gate.com/dynamic/products/dlm/dlm-automation-suite/download"
}

# Check the parameters.
if (-NOT $SQLReleaseSourceDatabaseName) { throw "You must enter a value for 'Source database name'." }
if (-NOT $SQLReleaseSourceDatabaseServer) { throw "You must enter a value for 'Source SQL Server instance'." }
if (-NOT $SQLReleaseTargetDatabaseName) { throw "You must enter a value for 'Target database name'." }
if (-NOT $SQLReleaseTargetDatabaseServer) { throw "You must enter a value for 'Target SQL Server instance'." }

$queryBatchTimeout = 30
if (![string]::IsNullOrWhiteSpace($SQLReleaseQueryBatchTimeout)) {
    if (![int32]::TryParse($SQLReleaseQueryBatchTimeout , [ref]$queryBatchTimeout )) {
        throw 'The query batch timeout must be a numerical value (in seconds).'
    }
    if ($queryBatchTimeout -lt 0) {
        throw "The query batch timeout can't be negative."
    }
}

$targetDB = New-DatabaseConnection -ServerInstance $SQLReleaseTargetDatabaseServer -Database $SQLReleaseTargetDatabaseName -Username $SQLReleaseTargetUsername -Password $SQLReleaseTargetPassword | Test-DatabaseConnection
$sourceDB = New-DatabaseConnection -ServerInstance $SQLReleaseSourceDatabaseServer -Database $SQLReleaseSourceDatabaseName -Username $SQLReleaseSourceUsername -Password $SQLReleaseSourcePassword | Test-DatabaseConnection

# Create the deployment resources, only adding the arguments that are not null or empty.
if ($SQLReleaseFilterPath) {
    if ($SQLReleaseCompareOptions) {
        $release = New-DatabaseRelease  -Target $targetDB `
                                        -Source $sourceDB `
                                        -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel `
                                        -FilterPath $SQLReleaseFilterPath `
                                        -SQLCompareOptions $SQLReleaseCompareOptions `
                                        -Verbose
    } else {
        $release = New-DatabaseRelease  -Target $targetDB `
                                        -Source $sourceDB `
                                        -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel `
                                        -FilterPath $SQLReleaseFilterPath `
                                        -Verbose
    }
} else {
    if ($SQLReleaseCompareOptions) {
        $release = New-DatabaseRelease  -Target $targetDB `
                                        -Source $sourceDB `
                                        -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel `
                                        -SQLCompareOptions $SQLReleaseCompareOptions 
                                        -Verbose
    } else {
        $release = New-DatabaseRelease  -Target $targetDB `
                                        -Source $sourceDB `
                                        -TransactionIsolationLevel $SQLReleaseTransactionIsolationLevel `
                                        -Verbose
    }
}
        
# Deploy the source schema to the target database.
$release | Use-DatabaseRelease -DeployTo $targetDB -SkipPreUpdateSchemaCheck -QueryBatchTimeout $queryBatchTimeout -Verbose
