Properties.Octopus.Action.Script.ScriptBody:
[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12

Function GetLatestVersion(){
    if([string]::IsNullOrEmpty($SpecificVersionInstallation)){
        Write-Host "SpecificVersionInstallation is empty, getting latest version from octopusdeploy.com"
        $versions = Invoke-WebRequest https://octopusdeploy.com/download/upgrade/v3 -UseBasicParsing | ConvertFrom-Json
        $version = $versions[-1].Version
        return $version
    }
    else {
        Write-Host "Specifc version is selected, chosen version " $SpecificVersionInstallation
        return $SpecificVersionInstallation
    }
}

Function GetInstalledVersion () {
    $InstalledVersion = (get-item "$InstallPath\Octopus.Server.exe").VersionInfo.fileversion
    return $installedVersion
}

Function DownloadOctopusDeploy([string] $versionNumber) {

    Write-Host "Downloading Octopus $version"
    $tempFile = [System.IO.Path]::GetTempFileName()
  	try
    {
        (New-Object System.Net.WebClient).DownloadFile("https://download.octopusdeploy.com/octopus/Octopus.$version-x64.msi", $tempFile)
    }
    catch
    {
        Write-Host "Exception occured"
        echo $_.Exception|format-list -force
    }

    Write-Host "Download completed"
    return $tempFile
}


Function StopOctopusServer() {
    Write-Host "Stopping Server"
    . "$InstallPath\Octopus.Server.exe" service --stop --console --instance $InstanceName
}

Function InstallOctopusDeploy([object] $tempFile){
    Write-Host "Installing"
    msiexec /i $tempFile /quiet | Out-Null
}

Function RemoveTempFile([object] $tempFile)
{   
    Write-Host "Deleting installer"
    Remove-Item $tempFile
}

Function StartOctopusDeploy(){
    Write-Host "Starting Server"
    . "$InstallPath\Octopus.Server.exe" service --start --console --instance $InstanceName
}


$version = GetLatestVersion
$installedVersion = GetInstalledVersion


if([version]$version -eq [version]$installedVersion){
    Write-host "Octopus already updated to version: $version"   
}

else  {
    Write-Host "Installed version is $installedVersion"
    Write-Host "Installing version $version" 

    $tempFile = DownloadOctopusDeploy $version
    StopOctopusServer
    InstallOctopusDeploy $tempFile
    RemoveTempFile $tempFile
    StartOctopusDeploy
}
